import React from 'react';
import "./nav.css"
import { Link,useNavigate } from 'react-router-dom';
import { useState } from 'react';


function Nav() {
    const navigate=useNavigate()
    const[log,setLog]=useState()
  return (
    <ul class="snip1143">
  <li class="current"><Link to="/Home" data-hover="Home">Home</Link></li>
  <li><Link to="/allcourses" data-hover="Our Courses">Our Courses</Link></li>
  <li style={{ display: localStorage.getItem('token') ? 'block' : 'none' }}><Link to="/usercourses" data-hover="My Courses">My Courses</Link></li>
  <li><Link to="/signup" data-hover="Sign-up">Sign-up</Link></li>
  <li style={{ display: localStorage.getItem('token') ? 'none' : 'inline-block' }}><Link to="/login" data-hover="Log-In">Log-In</Link></li>
  <li style={{ display: localStorage.getItem('token') ? 'block' : 'none' }} onClick={()=>{
          localStorage.removeItem('token')
          navigate("/Home")}}><a data-hover="Log-Out">Log-Out</a></li>
</ul>
  );
}

export default Nav;
