import React from 'react';
import Slideshow from './slideShow';
import { AppContext } from '../helpers/context';
import { useState,useContext,useEffect } from 'react';
import axios from "axios"
import {TypeAnimation} from "react-type-animation"

const Home = () => {
    const {userEmail,setUser_id,user_id,setUserCourses,userCourses,getUserId,getUserCourses,}=useContext(AppContext)
    
  useEffect(() => {
    getUserId();
    }, []);

    
  useEffect(() => {
    getUserCourses();
    }, [user_id]);
    return (
      <>
     
        <div style={{height: '60%', width: '60%', marginTop:"10%"}}>
        
            <Slideshow/>
        </div>
        <div>
        <TypeAnimation
        id='intro'
        sequence={[
          1000,
          "Welcome to our website, your one-stop destination for all things programming. We offer a wide variety",
          2000,
          "of courses for beginners to advanced programmers, covering various programming languages and",
          3000,
          "technologies. Our expert instructors have real-world experience and are dedicated to helping you",
          4000,
          "master the skills you need to succeed in today's digital landscape. Whether you're looking to start a new",
          5500,
          "career or advance in your current one, our courses are designed to help you achieve your goals. Browse our selection and start learning today!",
        ]}
        wrapper="h2"
        stlye={{margin: '3%'}}/>
        </div>
        
   </>
         );
}

export default Home;
