import React, { createContext, useEffect, useState } from "react";
import axios from "axios"
export const AppContext = createContext();
const Context = (props) => {
  const { children } = props;
  const[userName,setUserName]=useState()
  const[userEmail,setUserEmail]=useState()
  const [user_id,setUser_id]=useState()
  const[userCourses,setUserCourses]=useState()
  const [divHandler1,setDivHandler1]=useState(false)
  const [divHandler2,setDivHandler2]=useState(false)
  const [divHandler3,setDivHandler3]=useState(false)
  const [divHandler4,setDivHandler4]=useState(false)
  
  const getUserId=async()=>{
    const data={email:userEmail}
    try {
      const response = await axios.post('http://localhost:5000/api/users/getUserByEmail', data);
      setUser_id(response.data.user_id)
    } catch (error) {
      console.log(error.message) 
    }

  };
  const getUserCourses=async()=>{
    const data={user_id:user_id}
    try {
      const response = await axios.post('http://localhost:5000/api/users/getUserCourses', data);
      setUserCourses(response.data)
      console.log(userCourses)
    } catch (error) {
      console.log(error.message) 
    }

  };

   setUserCourses=()=>{
    if (userCourses.some(course => course.name === "Python Programming")) {
      setDivHandler1(true);
    }
    if (userCourses.some(course => course.name === "JavaScript Fundamentals")) {
      setDivHandler2(true);
    }
    if (userCourses.some(course => course.name === "Full Stack Web Development")) {
      setDivHandler3(true);
    }
    if (userCourses.some(course => course.name === "C# ")) {
      setDivHandler4(true);
    }
  }


 return (
    <AppContext.Provider
    
      value={{setUserName,userName,userEmail,setUserEmail,user_id,setUser_id,setUserCourses,userCourses,getUserId,getUserCourses,divHandler1,divHandler2,divHandler3,divHandler4
      }}
    >
      {children}
    </AppContext.Provider>
  );
};

export default Context;
