import React from 'react';
import Slideshow from './slideShow';
import { AppContext } from '../helpers/context';
import { useState,useContext,useEffect } from 'react';
import axios from "axios"

const Home = () => {
    const {userEmail,setUser_id,user_id,setUserCourses,userCourses}=useContext(AppContext)
    const getUserId=async()=>{
    const data={email:userEmail}
    try {
      const response = await axios.post('http://localhost:5000/api/users/getUserByEmail', data);
      setUser_id(response.data.user_id)
    } catch (error) {
      console.log(error.message) 
    }

  };
  useEffect(() => {
    getUserId();
    }, [userEmail]);

    const getUserCourses=async()=>{
    const data={user_id:user_id}
    try {
      const response = await axios.post('http://localhost:5000/api/users/getUserCourses', data);
      setUserCourses(response.data)
      console.log(userCourses)
    } catch (error) {
      console.log(error.message) 
    }

  };
  useEffect(() => {
    getUserCourses();
    }, [getUserId]);
    return (
        <div style={{height: '80%', width: '80%'}}>
        
            <Slideshow/>
        </div>
    );
}

export default Home;
