import React from 'react';
import { useEffect,useState,useContext } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { AppContext } from '../helpers/context';
import "./loginForm.css"



const Users = () => {
  const {setUserName,setUserEmail,setUser_id}=useContext(AppContext)
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirm,setConfirm]=useState('');
    const navigator=useNavigate()
    
    const handleSubmit = async (e) => {
      e.preventDefault();
  
      const confirmation = {
        email: email,
        password: password
    
      };
      
  
      try {
          const response = await axios.post('http://localhost:5000/api/login', confirmation);
           localStorage.setItem("token",response.data)
           console.log(response)
           const userD=await axios.post('http://localhost:5000/api/users/getUserByEmail',confirmation)
           await setUserName(userD.name)
           await setUserEmail(confirmation.email)
           await setUser_id(userD.user_id)

           navigator("/comments")
           
          
          
        } catch (error) {
          alert(error)
          setConfirm("Wrong user name or password")
          
        }
      
      };
    return (
      <>
      <form  onSubmit={handleSubmit}>
  <h1>Students Log in</h1>
  <div class="inset">
  <p>
    <label for="email">EMAIL ADDRESS</label>
    <input type="email"
            value={email}
            onChange={(event) => setEmail(event.target.value)}
            required
            name="email" 
            id="email"/>
  </p>
  <p>
    <label for="password">PASSWORD</label>
    <input  type="password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
            required
            name="password" 
            id="password"/>
  </p>
  <p>
    <input type="checkbox" name="remember" id="remember"/>
    <label for="remember">Remember me for 14 days</label>
  </p>
  </div>
    <input type="submit" name="go" id="go" value="Log in"/>
  
</form>
      </>)
}

export default Users;
