import React from 'react';
import "./navbar.css"
import { Link,useNavigate } from 'react-router-dom';
import { useContext } from 'react';
import { AppContext } from '../helpers/context';
import axios from "axios"


function Navbar() {
  
  const navigate=useNavigate()
  const {userEmail}=useContext(AppContext)
  const getUserId=async()=>{
    try {
      const response = await axios.post('http://localhost:5000/api/users/getUserByEmail', {email:userEmail});
      console.log(response)
    } catch (error) {
      console.log(error.message) 
    }
  };

  
  return (
    <nav>
      <ul>
        <li id="a"><Link to="/Home">home</Link></li>
        <li id="a"><Link to="/allcourses">Courses</Link></li>
        <li id="b" hidden={localStorage.getItem('token')?true:false}><Link to="/usercourses">My Courses</Link></li>
        <div id="container">
        <li><button>Sign-in</button></li>
        <li><button>Sign-Up</button></li>
        <li id="b"><button onClick={()=>{
          localStorage.removeItem('token')
          navigate("/login")
        }}>LogOut</button></li>
        </div>
      </ul>
    </nav>
  );
}

export default Navbar;
