import React from 'react';
import { Slide } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css';
import "./slide.css"


const slideImage = [
    {
      url: "../helpers/images/python.jpg",
      caption: 'Slide 1'
    },
    {
      url: "../helpers/images/fullStack.jpg",
      caption: 'Slide 2'
    },
    {
      url: "../helpers/images/c.jpeg",
      caption: 'Slide 3'
    },
    {
      url: "../helpers/images/js.png",
      caption: 'Slide 4'
    },
  ];


const Slideshow = () => {
    return (
       
              <div className="slide-container" style={{height: '500px', width: '100%'}}>
                <Slide>
                 {slideImage.map((slideImage, index)=> (
                    <div className="each-slide" style={{height: '100%', width: '100%'}} key={index}>
                     <img src={slideImage.url} style={{height: '100%', width: '100%'}} />
                        <span>{slideImage.caption}</span>
                    </div>
                  ))} 
                </Slide>
              </div>
         
);
};


export default Slideshow;
