import React from 'react';
import { useState,useEffect,useContext } from 'react';
import axios from 'axios'
import "./userCourseCard.css"
import c from "../helpers/images/c.jpeg"
import fullStack from "../helpers/images/fullStack.jpg"
import js from "../helpers/images/js.png"
import python from "../helpers/images/python.jpg"
import { AppContext } from '../helpers/context';
import { useNavigate } from 'react-router-dom';
import "./popup.css"


const UserCourseCard = (props) => {
    const {user_id}=useContext(AppContext)
    const navigator=useNavigate()
    const{name,rating,level}=props
    let starRate = []
    for(let i=0;i<=rating;i++){
        starRate.push(<i class="fa fa-star" aria-hidden="true"></i>)
    }
    let image=[]
    
    if (name === 'C#  ') {
        image.push(<img src={c} alt={name}/>)
      } else if (name === "Full Stack Web Development ") {
        image.push(<img src={fullStack} alt={name}/>)
      } else if (name === "JavaScript Fundamentals ") {
        image.push(<img src={js} alt={name}/>)
      } else if (name == "Python Programming ") {
        image.push(<img src={python} alt={name}/>)
      }
   
      const deleteCourse=async()=>{

        const data={
            user_id:user_id,
            name:name};
           
        try {
          const response = await axios.delete('http://localhost:5000/api/users/leavecourse', {data});
          console.log(response)
          navigator("/Home")
        } catch (error) {
          console.log(error.message) 
        }
      }
    
    return (
        <div id="container">	
        <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"/>
	
	<div class="product-details">
	<h1>{name}</h1>
	<span class="hint-star star">
		{starRate}
	</span>
		<h3>Level: {level}</h3>
        <h5>this is the best course in the world it will make you a proffesional programmar and developer and will be able to earn 50k$ a month!</h5>
    <div class="control">
	<button for="modal-open" class="modal btn ">
	 <span class="price">X</span>
   <span class="shopping-cart"><i class="fa fa-trash"  aria-hidden="true"></i></span>
   <span class="buy">Leave</span>
 </button>
 
 
</div>		
</div>

	
<div class="product-image">
	
        {image}
</div>
<div class="content">
    <h1>Modal</h1>
    <label class="modal-open modal-label" for="modal-open">
           <p>Open Modal</p>
       </label>
    <input type="radio" name="modal" value="open" id="modal-open" class="modal-radio" />
    <div class="modal">
      <label class="modal-label overlay">
                <input type="radio" name="modal" value="close" class="modal-radio"/>
            </label>
      <div class="content">
        <div class="top">
          <h2>Heading </h2>
          <label class="modal-label close-btn">
                    <input type="radio" name="modal" value="close" class="modal-radio"/>
                </label>
        </div>
        <p>sdassdasasassda</p>
        
      </div>
      
    </div>
  </div>
</div>





    );
}

export default UserCourseCard;
