import { AppContext } from '../helpers/context';
import { useContext } from 'react';
import React from 'react';
import "./allCourses.css"
import axios from "axios"
import { useState,useEffect } from 'react';

const AllCourses = () => {
    
    const {user_id,getUserId,getUserCourses,setUserCourses1}=useContext(AppContext)
    
    
    useEffect(() => {
      getUserId();
      
      }, []);
  
      
    useEffect(() => {
      getUserCourses();
      setUserCourses1()
      }, [user_id]);
    
    
    const handleSubmit =  async (subject) => {
        // event.preventDefault();
        const config = {
          headers: {
            'x-auth-token': localStorage.getItem("token")
            
          }
        };
        
        const data=  {
          user_id: user_id,
          subject: subject,
         
        };
        console.log(data)
        try {
          const response = await axios.post('http://localhost:5000/api/users/signup', data, config);
          if(response.data=='Successfully signed up for course!'){

          }
        } catch (error) {
          console.log(error.message) 
        }
      };




    return (
        <div class="container">
  <div class="card">
    <div class="box">
      <div class="content">
        <h2>Python</h2>
        <h3>Python Programming</h3>
        <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Labore, totam velit? Iure nemo labore inventore?</p>
        <a  onClick={()=>{handleSubmit("Python Programming")}}>Sign-Up</a>
      </div>
    </div>
  </div>

  <div class="card">
    <div class="box">
      <div class="content">
        <h2>JavaScript Fundamentals</h2>
        <h3>JavaScript Fundamentals</h3>
        <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Labore, totam velit? Iure nemo labore inventore?</p>
        <a  onClick={()=>{handleSubmit("JavaScript Fundamentals")}}>Sign-Up</a>
      </div>
    </div>
  </div>

  <div class="card">
    <div class="box">
      <div class="content">
        <h2>Full Stack Web Development</h2>
        <h3>Full Stack Web Development</h3>
        <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Labore, totam velit? Iure nemo labore inventore?</p>
        <a  onClick={()=>{handleSubmit("Full Stack Web Development")}}>Sign-Up</a>
      </div>
    </div>
  </div>

  <div class="card">
    <div class="box">
      <div class="content">
        <h2>c#</h2>
        <h3>c#</h3>
        <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Labore, totam velit? Iure nemo labore inventore?</p>
        <a  onClick={()=>{handleSubmit("C# ")}}>Sign-Up</a>
      </div>
    </div>
  </div>
</div>
       
    );
}

export default AllCourses;
