import React from 'react';
import { useState,useEffect } from 'react';
import axios from 'axios'
import "./userCourseCard.css"
const c=require("../helpers/images/c.jpeg")
const fullStack=require("../helpers/images/fullStack.jpg")
const js=require("../helpers/images/js.png")
const python=require("../helpers/images/python.jpg")

const UserCourseCard = (props) => {
    const [image,setImage]=useState()
    const{name,rating,level}=props
    let starRate = []
    for(let i=0;i<=rating;i++){
        starRate.push(<i class="fa fa-star" aria-hidden="true"></i>)
    }
    switch (name) {
        case "c":
            setImage(c)
          break;
        case "python":
            setImage(python)
          break;
        case "js":
            setImage(js)
          break;
        case "fullStack":
            setImage(fullStack)
          break;
    }

    return (
        <div id="container">	
        <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"/>
	
	<div class="product-details">
	<h1>{name}</h1>
	<span class="hint-star star">
		{starRate}
	</span>
		<h3>Level: {level}</h3>
        <h5>this is the best course in the world it will make you a proffesional programmar and developer and will be able to earn 50k$ a month!</h5>
    <div class="control">
	<button class="btn">
	 <span class="price">X</span>
   <span class="shopping-cart"><i class="fa fa-trash"  aria-hidden="true"></i></span>
   <span class="buy">Leave</span>
 </button>
</div>		
</div>
	
<div class="product-image">
	
<img src={image} alt="name"/>
	


</div>

</div>




    );
}

export default UserCourseCard;
