import React from 'react';
import "./popup.css"

const Popup = (props) => {
    return (props.trigger)? (
        <div className='popup'>
            <div className='popup-inner'>
                <button className='close-button'>close</button>
                {props.children}
            </div>
        </div>
    ):"";
}

export default Popup;
