import React from 'react';
import { Slide } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css';


const images = [
    {
      url: "../helpers/images/python.jpg",
      caption: 'Slide 1'
    },
    {
      url: "../helpers/images/fullStack.jpg",
      caption: 'Slide 2'
    },
    {
      url: "../helpers/images/c.jpeg",
      caption: 'Slide 3'
    },
    {
      url: "../helpers/images/js.png",
      caption: 'Slide 4'
    },
  ];


const Slideshow = () => {
    return (
        <Slide>
        <div className="each-slide-effect">
            <div style={{ 'backgroundImage': `url(${images[0].url})` }}>
                <span>Slide 1</span>
            </div>
        </div>
        <div className="each-slide-effect">
            <div style={{ 'backgroundImage': `url(${images[1].url})` }}>
                <span>Slide 2</span>
            </div>
        </div>
        <div className="each-slide-effect">
            <div style={{ 'backgroundImage': `url(${images[2].url})` }}>
                <span>Slide 3</span>
            </div>
        </div>
        <div className="each-slide-effect">
            <div style={{ 'backgroundImage': `url(${images[3].url})` }}>
                <span>Slide 3</span>
            </div>
        </div>
    </Slide>
);
};


export default Slideshow;
