import { AppContext } from '../helpers/context';
import { useContext } from 'react';
import React from 'react';
import "./allCourses.css"
import axios from "axios"
import { useState,useEffect } from 'react';

const AllCourses = () => {
    
    const [divHandler1,setDivHandler1]=useState()
    const [divHandler2,setDivHandler2]=useState()
    const [divHandler3,setDivHandler3]=useState()
    const [divHandler4,setDivHandler4]=useState()
    const {user_id,getUserId,getUserCourses,userCourses}=useContext(AppContext)
    
    
    
    useEffect(() => {
      getUserId();
      
      }, []);
  
      
    useEffect(() => {
      getUserCourses();
      if (userCourses.some(course => course.name === "Python Programming")) {
        setDivHandler1(true);
      }
      if (userCourses.some(course => course.name === "JavaScript Fundamentals")) {
        setDivHandler2(true);
      }
      if (userCourses.some(course => course.name === "Full Stack Web Development")) {
        setDivHandler3(true);
      }
      if (userCourses.some(course => course.name === "C# ")) {
        setDivHandler4(true);
      }
      
      }, [user_id]);
    
      
      
    
      
   
    return (
        <div  class="container">
  <div style={{ display: divHandler1 ? 'inline-block' : 'none' }} class="card">
    <div  class="box">
      <div class="content">
        <h2>Python</h2>
        <h3>Python Programming</h3>
        <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Labore, totam velit? Iure nemo labore inventore?</p>
        
      </div>
    </div>
  </div>

  <div style={{ display: divHandler2 ? 'inline-block' : 'none' }} class="card">
    <div class="box">
      <div class="content">
        <h2>JavaScript Fundamentals</h2>
        <h3>JavaScript Fundamentals</h3>
        <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Labore, totam velit? Iure nemo labore inventore?</p>
        
      </div>
    </div>
  </div>

  <div style={{ display: divHandler3 ? 'inline-block' : 'none' }} class="card">
    <div class="box">
      <div class="content">
        <h2>Full Stack Web Development</h2>
        <h3>Full Stack Web Development</h3>
        <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Labore, totam velit? Iure nemo labore inventore?</p>
        
      </div>
    </div>
  </div>

  <div style={{ display: divHandler4 ? 'inline-block' : 'none' }} class="card">
    <div class="box">
      <div class="content">
        <h2>C#</h2>
        <h3>C#</h3>
        <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Labore, totam velit? Iure nemo labore inventore?</p>
        
      </div>
    </div>
  </div>
  <div style={{ display: userCourses[0] ? 'none' : 'inline-block' }}><h1>Hello user you dont have any courses yet! Go back to the All Courses page and sign up!</h1></div>
</div>
       
    );
}

export default AllCourses;
