import React from 'react';
import "./navbar.css"
import { Link,useNavigate } from 'react-router-dom';


function Navbar() {
  const navigate=useNavigate()
  return (
    <nav>
      <ul>
        <li id="1"><Link to="/Home">home</Link></li>
        <li id="2" hidden={localStorage.getItem('token')?true:false}><Link to="/usercourses">My Courses</Link></li>
        <li id="3"><button onClick={()=>{
          localStorage.removeItem('token')
          navigate("/login")
        }}>LogOut</button></li>
      </ul>
    </nav>
  );
}

export default Navbar;
