import axios from "axios"

const Home = () => {
    const {userEmail,setUser_id,user_id,setUserCourses,userCourses,getUserId,getUserCourses,}=useContext(AppContext)
    
  useEffect(() => {
    getUserId();
    }, []);

    
  useEffect(() => {
    getUserCourses();
    }, [user_id]);
    return (
      <>
      <div className='homeContainer'><h2>Welcome to our website, your one-stop destination for all things programming. We offer a wide variety of courses for beginners to advanced programmers, covering various programming languages and technologies. Our expert instructors have real-world experience and are dedicated to helping you master the skills you need to succeed in today's digital landscape. Whether you're looking to start a new career or advance in your current one, our courses are designed to help you achieve your goals. Browse our selection and start learning today!</h2></div>
        <div style={{height: '70%', width: '70%'}}>
        
            <Slideshow/>
        </div></>
    );
}

export default Home;
