import { AppContext } from '../helpers/context';
import { useContext } from 'react';
import React from 'react';
import "./allCourses.css"
import axios from "axios"
import { useState,useEffect } from 'react';

const AllCourses = () => {
    
    const [divHandler,setDivHandler]=useState()
    const {user_id,getUserId,getUserCourses,userCourses}=useContext(AppContext)
    
    
    
    useEffect(() => {
      getUserId();
      
      }, []);
  
      
    useEffect(() => {
      getUserCourses();
      if (userCourses.some(course => course.name === "Python Programming")) {
        setDivHandler(true);
      }
      
      }, [user_id]);
    
      
      
    
      
   
    return (
        <div  class="container">
  <div style={{ display: divHandler ? 'none' : 'inline-block' }} class="card">
    <div  class="box">
