import { Slide } from 'react-slideshow-image'
import 'react-slideshow-image/dist/styles.css'


const slideImages = [
    {
      url: "../helpers/images/python.jpg",
      caption: 'Slide 1'
    },
    {
      url: "../helpers/images/fullStack.jpg",
      caption: 'Slide 2'
    },
    {
      url: "../helpers/images/c.jpeg",
      caption: 'Slide 3'
    },
    {
      url: "../helpers/images/js.png",
      caption: 'Slide 4'
    },
  ];


const Slideshow = () => {
    return (
      <div className="slide-container" style={{'Height': '100%'}}>
        <Slide>
         {slideImages.map((slideImage, index)=> (
            <div className="each-slide" style={{'Height': '100%'}} key={index}>
             <div style={Object.assign({'img': `url(${slideImage.url})`}, {'Height': '100%'})}>
                <span>{slideImage.caption}</span>
              </div>
            </div>
          ))} 
        </Slide>
      </div>
    )
}

export default Slideshow;
