import React from 'react';
import "./nav.css"
import { Link,useNavigate } from 'react-router-dom';


function Nav() {
  return (
    <ul class="snip1143">
  <li class="current"><Link to="/Home" data-hover="Home">Home</Link></li>
  <li><Link to="/allcourses" data-hover="Our Courses">Our Courses</Link></li>
  <li style={{ display: localStorage.getItem('token') ? 'block' : 'none' }}>
  <Link to="/usercourses" data-hover="My Courses">My Courses</Link></li>
  <li><a href="#" data-hover="Products">Sign-up</a></li>
  <li><a href="#" data-hover="Contact">Login</a></li>
</ul>
  );
}

export default Nav;
